import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      joke: '',
    };
  }

  componentDidMount() {
    const API_URL = 'https://images.dog.ceo/breeds/malamute/n02110063_11668.jpg';
    fetch(API_URL)
      .then((response) => response.json())
      .then((data) => this.setState({ img: data.message }));
  }

  render() {
    const { joke } = this.state;

    return (
      <div className="App">
        <h2>Joke:</h2>
        {img}
      </div>
    );
  }
}

export default App;
