import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
/* import PropTypes from 'prop-types'; */

class Registered extends Component {
  render() {
    const { clients, logged } = this.props;
    if (!logged) return (
      <div>
        <p>Login não efetuado</p>
        <Link to="/login">Ir para página Login </Link>
      </div>
    )
    const client = clients.map(({ name, age, email }, index) => (
    <div key={ index }>
      <p>{ name }</p>
      <p>{ age }</p>
      <p>{ email }</p>
    </div>))
    return (
      <div>
          {
            (client.lenght === 0) ? client :
              <p>Nenhum cliente cadastrado</p>
          }
          <Link to="/register">Ir para página de cadastro</Link>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  clients: state.reducer.clients,
  logged: state.reducerCheck.logged,
});

export default connect(mapStateToProps, null)(Registered);
