import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import addClient from "../Redux/actions/addClient";

class Register extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      age: '',
      clients: [],
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this); 
  }

  handleChange({ target }) {
    const { value, id } = target;
    this.setState({ [id]: value });
  }

  handleClick() {
    const { name, age, email } = this.state;
    const client = { name, age, email };
    this.setState((prev) => ({ clients: [...prev.clients, client ] }));
  }

  render() {
    return (
      <form>
        <label htmlFor="name">Name:</label>
        <input
          id="name"
          type="text"
          onChange={ (event) => this.handleChange(event) }
        />
        <label htmlFor="email">Email:</label>
        <input
          id="email"
          type="email"
          onChange={ (event) => this.handleChange(event) }
        />
        <label htmlFor="age">Age:</label>
        <input
          id="age"
          type="number"
          onChange={ (event) => this.handleChange(event) }
        />
        <button type="button" onClick={ () => this.handleClick() }>Gerar Cadastro</button>
        <Link to="/registered">Ver clientes cadastrados</Link>
      </form>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  handleCick = (state) => dispatch(addClient(state));
}

export default connect(null, mapDispatchToProps)(Register);
