import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

class Registered extends Component {
  render() {
    const { clients, logged } = this.props;
    if (!logged) return <p>Nenhum cliente cadastrado</p>;
    const client = clients.map(({ name, age, email }, index) => (
    <div key={ index }>
      <p>{ name }</p>
      <p>{ age }</p>
      <p>{ email }</p>
    </div>))
    return (
      <div>
          {
            client ? client :
              <p>Nenhum cliente cadastrado</p>
          }
          <Link to="/register">Ir para página de cadastro</Link>
      </div>
    );
  }
}

Registered.propTypes = {
  clients: PropTypes.instanceOf(Array),
  logged: PropTypes.instanceOf(Object),
}

Registered.defaultProps = {
  clients: [{ name: '' }],
  logged: { logged: false }
}

const mapStateToProps = (state) => ({
  clients: state.reducer.clients,
  logged: state.reducerCheck.logged,
});

export default connect(mapStateToProps, null)(Registered);
