import React, { Component } from "react";
import typesPokemons from './Type';
import Button from './Button';
import pokemons from "../data";

class NextPokemon extends Component {
  constructor(props) {
    super(props);
    this.handleEvent = this.handleEvent.bind(this);
    this.state = {
      pokemon: pokemons,
    }
  }

  handleEvent(type) {
    if(type !== 'All') {
      this.setState(() => ({
        pokemon: typesPokemons(type),
      }))
    } else {
      this.setState(() => ({
        pokemon: pokemons,
      }))
    }
  }

  render() {
    return (
      <div>
        <button type="submit" onClick={(event) => this.handleEvent(event.target.innerHTML)}>All</button>
        <button type="submit" onClick={(event) => this.handleEvent(event.target.innerHTML)}>Fire</button>
        <button type="submit" onClick={(event) => this.handleEvent(event.target.innerHTML)}>Electric</button>
        <button type="submit" onClick={(event) => this.handleEvent(event.target.innerHTML)}>Poison</button>
        <button type="submit" onClick={(event) => this.handleEvent(event.target.innerHTML)}>Psychic</button>
        <button type="submit" onClick={(event) => this.handleEvent(event.target.innerHTML)}>Normal</button>
        <button type="submit" onClick={(event) => this.handleEvent(event.target.innerHTML)}>Dragon</button>
        <Button disabled typePkm={ this.state.pokemon } />
      </div>
    );
  }
}

export default NextPokemon;
