import React from "react";
import pokemons from '../data';
import Pokemon from './Pokemon';

class Button extends React.Component {
  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      pokemon: this.props.type[0],
    }
  }

  handleClick() {
    if(this.props.type.indexOf(this.state.pokemon) !== this.props.type.length - 1) {
      this.setState((state, _props) =>({
        pokemon: this.props.type[this.props.type.indexOf(state.pokemon) + 1],
      }));
    }
    else {
      this.setState((state, _props) =>({
        pokemon: this.props.type[0],
      }));
    }
    console.log(this.state.pokemon);
  }

  render() {
    return (
      <div>
        <div className='div-pokedex'>
          <Pokemon pokemon={this.state.pokemon} />
        </div>
        <button onClick={this.handleClick}>Próximo Pokémon</button>
      </div>
    )
  }
}

export default Button;
