import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      age: 0,
      estado: '',
    }
  }

  handleEvent({ target }) {
    console.log(target.value);
  }

  render() {
    return (
      <form className='form'>

        <label for="name">Name:
          <input onChange={this.handleEvent} id="name" type="text" />
        </label>

        <label htmlFor="email">Email:
          <input id="email" type="email" />
        </label>

        <label for="age">Age:
          <input id="age" type="number" />
        </label>

        <label for="select">Estado:
          <select id='select'>
            <option>Bahia</option>
            <option>Rio de Janeiro</option>
            <option>São Paulo</option>
            <option>Paraná</option>
            <option>Minas Gerais</option>
          </select>
        </label>

        <label for="im">Sou brasileiro
          <input id="im" type="radio" />
        </label>

        <label for="im-not">Não sou brasileiro
          <input id="im-not" type="radio" />
        </label>

      </form>
    )
  }
}

export default Form;
