import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      age: 0,
      estado: '',
      souBr: false,
    }
    this.handleEvent = this.handleEvent.bind(this);
  }

  handleEvent({ target }) {
    console.log(target.value);
    this.setState({ name: target.value});
  }

  render() {
    return (
      <form className='form'>

        <label htmlFor="name">Name:
          <input onChange={this.handleEvent} id="name" name="name" type="text" />
        </label>

        <label htmlFor="email">Email:
          <input id="email" type="email" name="email"/>
        </label>

        <label htmlFor="age">Age:
          <input id="age" type="number" name="age" />
        </label>

        <label htmlFor="select">Estado:
          <select id='select'>
            <option>Bahia</option>
            <option>Rio de Janeiro</option>
            <option>São Paulo</option>
            <option>Paraná</option>
            <option>Minas Gerais</option>
          </select>
        </label>

        <label htmlFor="im">Sou brasileiro
          <input id="im" type="radio" />
        </label>

        <label htmlFor="im-not">Não sou brasileiro
          <input id="im-not" type="radio" />
        </label>

      </form>
    )
  }
}

export default Form;
