import React, { Component } from 'react';
import estados from './estados';

class Form extends Component {
  render() {
    const Estados = estados.map((estado, index) => <option key={index}>{estado}</option>);

    return (
      <form>
        <fieldset>
          <legend>Formulário de Cadastro</legend>

          <label htmlFor='name'>Name:
          <input maxLength='40' required type="text" name='name' id='name'/></label>

          <label htmlFor='email'>Email:
          <input maxLength='50' required type="email" name='email' id='email'/></label>

          <label htmlFor='cpf'>Cpf:
          <input maxLength='11' required type="text" name='cpf' id='cpf'/></label>

          <label htmlFor='endereço'>Endereço:
          <input  maxLength='200' required type="text" name='endereço' id='endereço'/></label>

          <label htmlFor='cidade'>Cidade:
          <input  maxLength='28' required type="text" name='cidade' id='cidade'/></label>

          <label htmlFor='estado'>Estado:
          <select required>
            {Estados}
          </select></label>

          <label htmlFor='cidade'>Cidade:
          <input  maxLength='28' required type="radio" name='casa' id='cidade'/>
          <input  maxLength='28' required type="radio" name='apartamento' id='cidade'/></label>

          <input type="submit" id='btn'/>

        </fieldset>
      </form>
    )
  }
}

export default Form;
