import React, { Component } from "react";
import Form from "./Form";

class HandleForm extends Component {
  constructor() {
    super();
    this.handleForm = this.handleForm.bind(this);
    this.handleCargo = this.handleCargo.bind(this);
    this.handleButton = this.handleButton(this);
    this.state = {
      name: '',
      email: '',
      cpf: '',
      endereço: '',
      cidade: '',
      estado: '',
      house: '',
      resume: '',
      cargo: '',
      cargoResume: '',
      visitCargo: 0,
    }
  }

  handleForm({ target }){
    const { name } = target;
    console.log(name);
    const value = (target.type === 'radio') ? target.id : target.value;
    console.log(value);
    if(name === 'name') {
      this.setState({ [name]: value.toUpperCase() });
    } else this.setState({ [name]: value });
  }

  handleName({ target }) {
    return target.value.toUpperCase();
  }

  handleState({ target }) {
    if (parseInt(target.value)) return '';
    return target.value;
  }

  handleCargo({target}) {
    this.setState((state, _props) => ({ visitCargo: state.visitCargo + 1 }));
    if(this.state.visitCargo === 0) alert('Preencha com cuidado essa informação!');
  }

  handleButton() {
    console.log(this.state);
  }

  render() {
    return (
      <div className="form-div">
        <Form handleButton={this.handleButton} handleState={this.handleState} handleName={this.handleName} handleCargo= {this.handleCargo} handleForm={this.handleForm}/>
      </div>
    )
  }
}

export default HandleForm;
