  render() {
    const father = (props) => {
      return (
        <div>
          { props.children }
        </div>
      )
    }
    return (
      <div className="App">
        <Pai>
          <p>Olá, senhor</p>
        </Pai>
        { father }
      </div>
    );
  }
}

export default App;
